mapper.update:
UPDATE GOODS_IMG
SET GOODS_IMG1=#{GOODS_IMG1},
GOODS_IMG2=#{GOODS_IMG2},
GOODS_IMG3=#{GOODS_IMG3}
WHERE
GOODS_NUM = #{GOODS_NUM}

update GOODS
set GOODS_DCPRICE =
(
select GOODS_PRICE*NVL((100-#{discount}/100,0)
from GOODS
where GOODS_NUM=#{GOODS_NUM}
)
where GOODS_NUM=#{GOODS_NUM}

update GOODS
set GOODS_DCPRICE = GOODS_PRICE*NVL((100-#{DISCOUNT})/100,0)
where CATEGORY_NUM = #{CATEGORY_NUM};

update GOODS
set GOODS_DCPRICE = GOODS_PRICE
where GOODS_NUM= #{GOODS_NUM}

update GOODS
set GOODS_DCPRICE = GOODS_PRICE
where CATEGORY_NUM= #{CATEGORY_NUM}}
