mapper.select:
SELECT A.GOODS_NUM, A.GOODS_NAME, A.GOODS_PRICE, A.GOODS_DCPRICE, A.GOODS_SIMPLE, A.CATEGORY_NUM, A.GOODS_SOLDOUT, A.GOODS_HITCOUNT, A.GOODS_AMOUNT, B.GOODS_IMG, C.BIG_CATEGORY, C.SMALL_CATEGORY FROM GOODS A, GOODS_IMG B, CATEGORY C WHERE A.GOODS_NUM = B.GOODS_NUM AND A.CATEGORY_NUM = C.CATEGORY_NUM AND A.CATEGORY_NUM IN
#{Num}
ORDER BY A.GOODS_HITCOUNT DESC

SELECT A.GOODS_NUM, A.GOODS_NAME, A.GOODS_PRICE, A.GOODS_DCPRICE, A.GOODS_SIMPLE, A.GOODS_SOLDOUT, A.CATEGORY_NUM, A.GOODS_HITCOUNT, B.GOODS_IMG, AVG(C.REVIEW_GRADE) AS GRADE, D.BIG_CATEGORY, D.SMALL_CATEGORY FROM GOODS A, GOODS_IMG B, REVIEW C, CATEGORY D WHERE A.GOODS_NUM = B.GOODS_NUM AND A.GOODS_NUM = C.GOODS_NUM AND A.CATEGORY_NUM = D.CATEGORY_NUM AND A.CATEGORY_NUM IN
#{Num}
GROUP BY A.GOODS_NUM, A.GOODS_NAME, A.GOODS_PRICE, A.GOODS_DCPRICE, A.GOODS_SIMPLE, A.CATEGORY_NUM, A.GOODS_SOLDOUT, A.GOODS_HITCOUNT, B.GOODS_IMG, D.BIG_CATEGORY, D.SMALL_CATEGORY ORDER BY GRADE DESC

SELECT A.GOODS_NUM, A.GOODS_NAME, A.GOODS_PRICE, A.GOODS_DCPRICE, A.GOODS_SIMPLE, A.CATEGORY_NUM, A.GOODS_SOLDOUT, A.GOODS_HITCOUNT, A.GOODS_AMOUNT, B.GOODS_IMG, C.BIG_CATEGORY, C.SMALL_CATEGORY FROM GOODS A, GOODS_IMG B, CATEGORY C WHERE A.GOODS_NUM=B.GOODS_NUM AND A.GOODS_PRICE > A.GOODS_DCPRICE AND A.CATEGORY_NUM = C.CATEGORY_NUM AND A.CATEGORY_NUM IN
#{Num}
ORDER BY A.GOODS_DCPRICE DESC

SELECT A.GOODS_NUM, A.GOODS_NAME, A.GOODS_PRICE, A.GOODS_DCPRICE, A.GOODS_SIMPLE, A.CATEGORY_NUM, A.GOODS_SOLDOUT, A.GOODS_HITCOUNT, A.GOODS_AMOUNT, B.GOODS_IMG, C.BIG_CATEGORY, C.SMALL_CATEGORY FROM GOODS A, GOODS_IMG B, CATEGORY C WHERE A.GOODS_NUM=B.GOODS_NUM AND A.GOODS_PRICE > A.GOODS_DCPRICE AND A.CATEGORY_NUM = C.CATEGORY_NUM AND A.CATEGORY_NUM IN
#{Num}
ORDER BY A.GOODS_DCPRICE ASC
